import React, { useEffect } from 'react';
import '../stylesheets/main.css';

function Main() {
  useEffect(() => {
    const items = document.querySelectorAll('.box-slow, .box-med, .box-fast');
    const container = document.getElementById('container');

    items.forEach((item) => {
      item.addEventListener('animationiteration', () => {
        const containerWidth = container.offsetWidth;
        const itemWidth = item.offsetWidth;
        const itemLeft = parseFloat(item.style.left || 0);

        if (itemLeft + itemWidth < 0) {
          item.style.left = containerWidth + 'px';
        }
      });
    });
  }, []);

  return (
    <ul className='container' id='container'>
      <li className='line'></li>
      <li className='box-slow'>VISUAL IDENTITY DESIGN</li>
      <li className='line'></li>
      <li className='box-med'>MARKETING AND ADVERTISING DESIGN</li>
      <li className='line'></li>
      <li className='box-slow'>USER INTERFACE GRAPHIC DESIGN</li>
      <li className='line'></li>
      <li className='box-fast'>PUBLICATION DESIGN</li>
      <li className='line'></li>
      <li className='box-med'>PACKAGING DESIGN</li>
      <li className='line'></li>
      <li className='box-slow'>MOTION GRAPHIC DESIGN</li>
    </ul>
  );
}

export default Main;
